domain('git.cit.bcit.ca'),
domain('git.coop'),
domain('git.dev.ctu.univ-fcomte.fr'),
domain('git.drk.sc'),
domain('git.empiresmod.com'),
domain('git.fosscommunity.in'),
domain('git.immc.ucl.ac.be'),
domain('git.jami.net'),
domain('git.linux-kernel.at'),
domain('git.nzoss.org.nz'),
domain('git.silence.dev'),
domain('lab.libreho.st'),
domain('opencode.net'),
domain('skylab.vc.h-brs.de'),
domain('vbscan.fisica.unimib.it'),
domain('git.cardiff.ac.uk'),
domain('git.igwn.org'),
domain('git.ligo.org'),
domain('forge.tedomum.net'),
domain('git.callpipe.com'),
domain('git.happy-dev.fr'),
domain('source.ind.ie'),
domain('mau.dev'),
domain('source.puri.sm'),
domain('git.oeru.org'),
domain('git.najer.info'),
domain('gitplac.si'),
domain('git.rockylinux.org'),
regexp('^https?://(www\.)?git\.(gnu|synz)\.io(/.*)?$'),
regexp('^https?://(www\.)?code\.((videolan|briarproject)\.org|foxkit\.us)(/.*)?$'),
regexp('^https?://(www\.)?source\.(small-tech|joinmastodon)\.org(/.*)?$'),
regexp('^https?://(www\.)?git\.(drupalcode|feneas|ouru|pwmt|regardscitoyens)\.org(/.*)?$'),
regexp('^https?://(www\.)?(joonpc\.skku\.edu/gitlab|mpegx\.int-evry\.fr/software)(/.*)?$'),
regexp('^https?://(web.archive.org/web/.*)?((next|www)\.)?(gitlab\.(?!(io|biterg)).*)(/.*)?$') {
  :root {
    --version: '🌚 Dark-GitLab v2.0.0-rc.14';
    --bshadow: 0 4px 8px var(--shadow);
    --shadow: rgba(0,0,0,0.251);
    --inverse: #fff;
    --avatar: 25%;
    --ac: #d47d3f;
    --ui-font: '"font_name"', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Noto Sans', 'Noto Sans', Ubuntu, Cantarell, 'Helvetica Neue', sans-serif, sans-serif, var(--emoji-font);
    --mono-font: '"font_name"', 'Menlo', 'DejaVu Sans Mono', 'Liberation Mono', 'Consolas', 'Ubuntu Mono', 'Courier New', 'andale mono', 'lucida console', monospace;
    --emoji-font: '"font_name"', 'Apple Color Emoji', 'Segoe UI Emoji', 'Segoe UI Symbol', 'Noto Color Emoji';
    --bg-0: hsla(0,0%,14.51%,1);
    --bg-1: hsla(0,0%,16.08%,1);
    --bg-2: hsla(0,0%,19.61%,1);
    --bg-3: hsla(0,0%,22.36%,1);
    --bg-4: hsla(0,0%,26.28%,1);
    --bg-5: hsla(0,0%,30.59%,1);
    --fg-0: hsla(0,0%,91.38%,1);
    --fg-1: hsla(0,0%,90.2%,1);
    --fg-2: hsla(0,0%,74.12%,1);
    --fg-3: hsla(0,0%,65.89%,1);
    --fg-4: hsla(0,0%,57.65%,1);
    --fg-5: hsla(0,0%,49.81%,1);
    --v0-0: hsla(25.5,64.52%,12.16%,1);
    --v0-1: hsla(25.39,63.42%,17.08%,1);
    --v0-2: hsla(24.93,63.11%,22.2%,1);
    --v0-3: hsla(24.62,62.91%,27.32%,1);
    --v0-4: hsla(25.44,63.89%,32.24%,1);
    --v0-5: hsla(25.15,63.64%,37.36%,1);
    --v0-6: hsla(24.92,63.45%,42.48%,1);
    --v0-7: hsla(24.74,63.29%,47.59%,1);
    --v0-8: hsla(24.76,63%,52.51%,1);
    --v0-9: hsla(25.23,63.57%,57.44%,1);
    --v1-0: hsla(0,60.87%,9.02%,1);
    --v1-1: hsla(0,59.43%,14.53%,1);
    --v1-2: hsla(0,60.44%,19.85%,1);
    --v1-3: hsla(0,60%,25.55%,1);
    --v1-4: hsla(0,59.43%,31.06%,1);
    --v1-5: hsla(0,60.25%,36.57%,1);
    --v1-6: hsla(0,59.79%,42.08%,1);
    --v1-7: hsla(0,60.2%,47.4%,1);
    --v1-8: hsla(0,60%,53.1%,1);
    --v1-9: hsla(0,60.32%,58.42%,1);
    --v2-0: hsla(30,60.87%,9.02%,1);
    --v2-1: hsla(29.27,59.43%,14.53%,1);
    --v2-2: hsla(30.55,60.44%,19.85%,1);
    --v2-3: hsla(29.57,60%,25.55%,1);
    --v2-4: hsla(30,59.43%,31.06%,1);
    --v2-5: hsla(29.7,60.25%,36.57%,1);
    --v2-6: hsla(30.01,59.79%,42.08%,1);
    --v2-7: hsla(30,60.2%,47.4%,1);
    --v2-8: hsla(30.01,60%,53.1%,1);
    --v2-9: hsla(30.01,60.32%,58.42%,1);
    --v3-0: hsla(60,60.87%,9.02%,1);
    --v3-1: hsla(60,59.43%,14.53%,1);
    --v3-2: hsla(60,60.44%,19.85%,1);
    --v3-3: hsla(60,60%,25.55%,1);
