    --v3-8: hsla(60,60%,53.1%,1);
    --v3-9: hsla(60,60.32%,58.42%,1);
    --v4-0: hsla(90,60.87%,9.02%,1);
    --v4-1: hsla(90.74,59.43%,14.53%,1);
    --v4-2: hsla(89.46,60.44%,19.85%,1);
    --v4-3: hsla(90.44,60%,25.55%,1);
    --v4-4: hsla(90,59.43%,31.06%,1);
    --v4-5: hsla(90.31,60.25%,36.57%,1);
    --v4-6: hsla(90,59.79%,42.08%,1);
    --v4-7: hsla(90,60.2%,47.4%,1);
    --v4-8: hsla(90,60%,53.1%,1);
    --v4-9: hsla(90,60.32%,58.42%,1);
    --v5-0: hsla(180,60.87%,9.02%,1);
    --v5-1: hsla(180,59.43%,14.53%,1);
    --v5-2: hsla(180,60.44%,19.85%,1);
    --v5-3: hsla(180,60%,25.55%,1);
    --v5-4: hsla(180,59.43%,31.06%,1);
    --v5-5: hsla(180,60.25%,36.57%,1);
    --v5-6: hsla(180,59.79%,42.08%,1);
    --v5-7: hsla(180,60.2%,47.4%,1);
    --v5-8: hsla(180,60%,53.1%,1);
    --v5-9: hsla(180,60.32%,58.42%,1);
    --v6-0: hsla(210,60.87%,9.02%,1);
    --v6-1: hsla(210.74,59.43%,14.53%,1);
    --v6-2: hsla(209.46,60.44%,19.85%,1);
    --v6-3: hsla(210.44,60%,25.55%,1);
    --v6-4: hsla(210,59.43%,31.06%,1);
    --v6-5: hsla(210.31,60.25%,36.57%,1);
    --v6-6: hsla(210,59.79%,42.08%,1);
    --v6-7: hsla(210,60.2%,47.4%,1);
    --v6-8: hsla(210,60%,53.1%,1);
    --v6-9: hsla(210,60.32%,58.42%,1);
    --v7-0: hsla(270,60.87%,9.02%,1);
    --v7-1: hsla(269.27,59.43%,14.53%,1);
    --v7-2: hsla(270.55,60.44%,19.85%,1);
    --v7-3: hsla(269.57,60%,25.55%,1);
    --v7-4: hsla(270,59.43%,31.06%,1);
    --v7-5: hsla(269.7,60.25%,36.57%,1);
    --v7-6: hsla(270,59.79%,42.08%,1);
    --v7-7: hsla(270,60.2%,47.4%,1);
