    --v8-3: hsla(300,60%,25.55%,1);
    --v8-4: hsla(300,59.43%,31.06%,1);
    --v8-5: hsla(300,60.25%,36.57%,1);
    --v8-6: hsla(300,59.79%,42.08%,1);
    --v8-7: hsla(300,60.2%,47.4%,1);
    --v8-8: hsla(300,60%,53.1%,1);
    --v8-9: hsla(300,60.32%,58.42%,1);
    --v9-0: hsla(0,0%,16.87%,1);
