/* Sidebar & Search Form */
div[data-testid="sidebarColumn"] form[role="search"] {padding:.5rem .3rem .5rem .4rem;}
div[data-testid="sidebarColumn"] > div:first-child > div:nth-child(2) > div:first-child > div:first-child > div:first-child {padding:0;}
