	vertical-align: -3px;
}
/*============================================*/
/*==================== 回答 ====================*/
/*===========================================*/

.zm-profile-vote-count {
	margin: 3px 0 0 0;
	color: #259;
}

.zm-votebar .up, .zm-votebar .down {
	margin: 5px 0 0 0;
}

.zm-profile-section-main.zm-profile-section-activity-main .question_link, .zm-profile-section-main.zm-profile-section-activity-main .post-link {
	font-weight: 600;
	line-height: 1.8em;
	margin-bottom: 5px;
}
/*===========================================*/
/*================== 右侧栏 ====================*/
/*===========================================*/

.zu-noti7-popup .zu-top-nav-live-inner {
	box-shadow: 0 0px 3px rgba(0, 0, 0, .3);
}

.zm-profile-side-following {
	border-bottom: 1px solid #ddd;
	margin: 10px 0 15px 0;
}

.vote-thanks-relation .zg-icon.vote {
	display: none;
}

.vote-thanks-relation .zg-icon.be-voted {
	display: none;
}

.zm-profile-side-section+.zm-profile-side-section {
	border-top: 1px solid #ddd;
}

.zu-main-sidebar .zh-footer .zg-wrap {
	border-top-color: #ddd;
	letter-spacing: 0;
	width: auto!important;
}

.zm-profile-side-following .item+.item {
	border-left: 0px solid #ddd;
	padding-left: 10px;
}
/*===========================================*/
/*================== 小名片 ====================*/
/*===========================================*/

i.icon.icon-profile-female {
	margin-top: -3px;
}

i.icon.icon-profile-male {
	margin-top: -2px;
}

.popover .popover-content {
	border-radius: 4px;
}

#zh-tooltip.goog-hovercard.popover {
	margin-top: -1px;
}

.zh-profile-card .lower .meta .item {
	margin-left: 6px;
	padding: 0 12px;
	border-right: 0px solid #eee;
}

.zh-profile-card .lower .meta .item .value {
	font-size: 15px;
	font-weight: 600;
	color: #600;
	letter-spacing: 0.02em;
	font-family: '.SFNSDisplay-Regular', 'Helvetica Neue', Helvetica, Arial, "Hiragino Sans GB", "Microsoft YaHei", "WenQuanYi Micro Hei", sans-serif;
}

.zh-profile-card .lower .meta .item .key {
	font-size: 13px;
	color: #999;
}

.zh-profile-card .lower {
	background: #FAFAFF;
}

.zu-noti7-popup.zu-top-nav-live {
	border: none;
}

.popover.bottom .arrow {
	display: none;
}

.popover.bottom .arrow2 {
	top: 0px;
	display: none;
}

.popover.top .arrow {
	display: none;
}

.popover.top .arrow2 {
	bottom: 0px;
	border-top: 13px solid #fcfcfc;
	display: none;
}

.popover .popover-content {
	border: none;
	box-shadow: 0 0px 2px rgba(0, 0, 0, .3);
}

.zm-noti7-popup-tab-item+.zm-noti7-popup-tab-item {
	border-left: 0px solid #ddd;
}

.zu-noti7-popup .zm-noti7-content-item {
	border-bottom: 1px dotted #ddd;
}

.zm-noti7-content-body .zm-noti7-content-item.unread {
	border-bottom: 1px dotted #ddd;
}

.zu-noti7-popup .zm-noti7-content-item::after {
	background: none;
}

.zm-noti7-frame-border {
	background: #ddd;
}
/*===========================================*/
/*================== 提问页面 ==================*/
/*===========================================*/

.modal-dialog-title {
	background-color: #1575d5;
	background-image: none;
	padding: 10px 15px;
	border-radius: 6px 6px 0 0;
	border: 0px solid #0D6EB8;
	box-shadow: none;
	margin: 0px;
}

.modal-dialog-title-text {
	font-size: 14px;
	font-weight: 600;
	color: #fff;
	text-shadow: none;
}

.modal-dialog {
	border: 0px solid #888;
	box-shadow: 0 0 4px 0 rgba(0, 0, 0, .4);
}

.zg-form-text-input {
	box-shadow: none;
	border-radius: 3px;
	border: 1px solid #ddd;
	color: #999;
}

textarea.zg-form-text-input, .zg-form-text-input>textarea {
	color: #444;
}

.zm-editable-editor-field-wrap {
	border: 1px solid #ddd;
	box-shadow: none;
}

.zm-add-question-form-topic-wrap .zm-tag-editor-editor {
	box-shadow: none;
	border: 1px solid #ddd!important;
	padding: 8px 8px 3px;
	position: relative;
}

.zh-add-question-form .zm-add-question-form-sns-wrap .goog-checkbox.goog-checkbox-checked {
	box-shadow: none;
}

.zm-item-tag, .zm-tag-editor-edit-item {
	background: #E4EAFF;
	color: #698ebf;
	transition: all .1s;
}

html.no-touch .zm-item-tag:hover, html.no-touch .zm-item-tag-x:hover {
	background: #698ebf;
	color: #fff;
	text-decoration: none;
}

.zg-icon-dropdown-menu {
	width: 30px;
}
/*===========================================*/
/*================== 问题页面 ==================*/
/*===========================================*/

.zu-edit-button {
	transition: all .1s;
}

.question-invite-panel {
	margin: 15px 0 35px;
	color: #444;
	border: 0px solid #ddd;
	border-bottom: 1px solid #ddd;
	border-top: 1px solid #ddd;
	box-shadow: none;
	border-radius: 0px;
}

.question-invite-panel .search-input {
	color: #666;
	background: #F9F9FF;
	border: 1px solid #ddddFF;
}

.zg-form-text-input:focus {
	box-shadow: none;
	border: 1px solid #E2E0FF;
	position: relative;
	background: #FCFCFF;
}

.goog-toolbar {
	border-top: 1px solid #ddd;
	border-left: 1px solid #ddd;
	border-right: 1px solid #ddd;
	box-shadow: none;
	background-color: #f3f3f3;
	background-image: none;
}

.goog-toolbar-button, .goog-toolbar-menu-button:hover {
	box-shadow: none;
}

.zu-global-notify {
	top: 0px;
	box-shadow: none;
}

.zm-item-answer-author-wrap {
	margin: 1px 0 0 0;
}

.zm-item-vote-info {
	margin: 7px 0;
}

.zh-answer-form {
	margin-bottom: 15px;
	margin-top: 15px;
}

.goog-menu {
	background: #fff;
	color: #666;
	border: solid 1px #eee;
	box-shadow: none;
}

.zm-tag-editor-remove-button {
	transition: none;
}
/*===========================================*/
/*================== 话题页面 ==================*/
/*===========================================*/

.side-topic-content.ellipsis {
	left: 0px;
}

.pin-topic-avatar-link {
	position: relative;
	top: 5px;
}

.side-topic-item {
	padding: 13px;
}

.zm-topic-cat-sub p {
	height: 3.4em;
}

/*===========================================*/
/*================ 知乎专栏 ==================*/
/*===========================================*/

html body, html input, html textarea, html select, html button {
	font-family: '.SFNSText-Regular', 'Helvetica Neue', Helvetica, Arial, "Hiragino Sans GB", "Microsoft YaHei", "WenQuanYi Micro Hei", sans-serif;
	letter-spacing: 0.02em;
	text-rendering: optimizeLegibility;
}

body, input, textarea, select, button {
	font-size: 16px;
	line-height: 1.9;
	color: #444;
}

a {
	color: #259;
	text-decoration: none!important;
	-webkit-transition: all .2s ease-in-out;
	-moz-transition: all .2s ease-in-out;
	-ms-transition: all .2s ease-in-out;
	-o-transition: all .2s ease-in-out;
	transition: all .2s ease-in-out;
}

i, em {
	font-style: normal!important;
}

strong, b {
	font-weight: 600;
	letter-spacing: 0.02em;
	font-family: '.SFNSDisplay-Regular', 'Helvetica Neue', Helvetica, Arial, "Hiragino Sans GB", "Microsoft YaHei", "WenQuanYi Micro Hei", sans-serif;
}

h1, h2, h3, h4, h5 {
	font-weight: 600;
	letter-spacing: 0.02em;
	font-family: '.SFNSDisplay-Regular', 'Helvetica Neue', Helvetica, Arial, "Hiragino Sans GB", "Microsoft YaHei", "WenQuanYi Micro Hei", sans-serif;
}

.entry-content {
	line-height: 2;
}

.review-comment-panel.fold {
	height: 60px;
}

.pop-panel-title {
	padding: 10px 0px 0px 18px;
}

.home .card-item, .home .column-card-item, .home .post-card-item {
	height: 279px;
}

.review-comment-panel .toggle, .review-comment-panel .close {
	top: 11px;
}
/*===========================================*/
/*================== 宽屏适配 ==================*/
/*===========================================*/

body, input, textarea, select, button {
	font-size: 18px;
	line-height: 2;
	color: #444;
}

.receptacle {
	width: 1080px;
}

.post-write .title {
	font-size: 32px!important;
}

.post-write .toolbar-holder {
	width: 1080px;
}

.post-write .entry-content {
	min-width: 1080px;
	margin-left: -540px;
}

.navbar-title-container {
	width: 1080px;
	margin-left: -540px;
}

.post-view .entry-title {
	font-size: 40px;
	line-height: 60px;
}

@media (max-width: 1400px) and (min-width: 968px) {
	.receptacle {
		width: 660px!important;
	}
	.post-write .toolbar-holder {
		width: 660px!important;
	}
	.post-write .entry-content {
		min-width: 660px!important;
		margin-left: -330px!important;
	}
	.navbar-title-container {
		width: 660px!important;
		margin-left: -330px!important;
	}
	body, input, textarea, select, button {
		font-size: 15px!important;
	}
	.post-view .entry-title {
		font-size: 32px!important;
		line-height: 45px!important;
	}
}

.Card {
	border-radius: 6px;
}

.UserCover-image, .UserCover-image img {
	opacity: 0.2;
	-webkit-transition: -webkit-opacity 0.5s ease-out;
	transition: -webkit-opacity 0.5s ease-out;
	transition: opacity 0.5s ease-out;
	transition: opacity 0.5s ease-out, -webkit-transform 6s ease-out;
	border-top-right-radius: 5px;
	border-top-left-radius: 5px;
}

.UserCover:hover .UserCover-image {
	opacity: 1;
}

.UserCover {
	height: 100px;
	border-top-right-radius: 5px;
	border-top-left-radius: 5px;
}

.UserCoverGuide-item {
	border-top-right-radius: 5px;
	border-top-left-radius: 5px;
}

.UserCoverGuide-dialog {
	box-shadow: 0 1px 3px rgba(0, 37, 55, .2);
}

.UserCover:hover {
	height: 240px;
}

.UserCover--colorBlock {
	height: 100px!important;
	background: #fff;
}

.Profile-followStatus {
	width: 49%;
}

.ContentItem-content {
	font-size: 14px;
}

.ContentItem-title {
	font-size: 15px!important;
	line-height: 1.0;
	font-weight: 600;
}

.AuthorInfo-name {
	font-size: 15px;
	font-weight: 600;
	line-height: 1.6;
}

.AuthorInfo-badge {
	margin-top: 2px;
	line-height: 1.6;
}

.Profile-lightItem, .Profile-lightList {
	padding-left: 1px;
	padding-right: 1px;
}

.ProfileHeader-name {
	font-weight: 600;
}

.ProfileHeader-headline {
	font-weight: 300;
}

.ContentItem-content.is-collapsed:hover {
	color: #444;
}

.Tabs-link {
	border-bottom: 3px solid transparent;
}

.Tabs-link:hover {
	border-bottom: 3px solid #259;
}

.Tabs-link.is-active:after {
	bottom: -3px;
	background: #259;
}

.Tabs-link:hover {
	color: #259;
}

.Tabs-link:hover .Tabs-meta {
	color: #259;
}

.Tabs-link.is-active {
	font-weight: 600;
}

.ContentItem-cover {
	display: none;
}

a {
	color: inherit;
}

.Profile-sideColumnItemLink:hover {
	color: #259;
}

.Profile-followStatus:hover .Profile-followStatusValue {
	color: #259;
}

.Profile-followStatusValue:hover {
	color: #259;
}

.Profile-lightItem:hover, .Profile-lightItem:hover .Profile-lightItemValue {
	color: #259;
}

a.Footer-item:hover {
	color: #259;
}

.Profile-footerOperations .Footer {
	padding-left: 1px;
	padding-right: 1px;
}

