/* ==UserStyle==
@name         new reddit full width
@version      20240404.15.41
@namespace    https://userstyles.world/user/alije123
@description  removes both sidebars and width limitation
@author       alije123
@license      No License
==/UserStyle== */

@-moz-document domain("reddit.com") {
.m\:grid-cols-\[repeat\(10\2c_1fr\)_repeat\(4\2cminmax\(61px\2c_1fr\)\)\] {
	grid-template-columns: none;
}

.xl\:max-w-\[1524px\],
.xl\:max-w-container-xl {
	max-width: 100% !important;
}

#left-sidebar-container,
#right-sidebar-container {
	display: none !important;
	width: 0px;
	visibility: hidden;
	overflow: hidden;
}

@media (min-width: 1200px) {
	.grid-container,
	.theme-rpl .grid .grid-cols-1 {
		grid-template-columns: 0px 1fr;
	}
}

.subgrid-container,
[class = "m:col-start-2"] .box-border .flex .flex-col .order-2 {
	width: 100%;
	max-width: calc(100vw - 0px);
}

aside,
.bg-neutral-background-weak {
	visibility: hidden;
	overflow: hidden;
	display: none;
}

main#main-content {
	width: 100%;
	max-width: 100%;
}
}

/* ==UserStyle==
@name         NGA深色模式
@version      20240808.09.20
@namespace    https://userstyles.world/user/nyx
@description  深色模式，部分页面可能没有适配，顺便隐藏了几个显眼的广告
@author       nyx
@license      No License
==/UserStyle== */

@-moz-document domain("bbs.nga.cn"), domain("ngabbs.com") {
/* 全局参数 */
:root {
    --darkest_grey: #2e2e2e;
    --darker_grey: #3a3a3a;
    --dark_grey: #464646;
    --grey: grey;
    --light_grey: #aaa;
    --lighter_grey: #c4c4c4;
    --lightest_grey: #e1e1e1;

    --darker_blue: #36454f;
    --dark_blue: #34546c;
    --blue: #3a6b91;
    --light_blue: #a4c6e1;
}
/* 调色 */
.gray{
    color: var(--light_grey);
}
/* 暗色 */
span.darkred,
span.crimson,
span.firebrick,
span.red, a.red,
span.blue, a.blue,
span.darkblue,
span.indigo,
span.purple
{
    filter: brightness(160%) contrast(50%) saturate(130%);
}
/* 浅暗色 */
span.silver,
span.orange, a.orange,
span.green, a.green,
span.deeppink
{
    filter: brightness(130%) contrast(90%) saturate(110%);
}
/* 亮色 */
span.orangered,
span.sandybrown,
span.limegreen,
span.skyblue
{
    filter: brightness(80%) contrast(90%) saturate(100%);
}
}

@-moz-document domain("bbs.nga.cn"), domain("ngabbs.com") {
/* 全局通用 */
body {
    background-color: var(--darkest_grey);
    color: var(--lightest_grey);
}
a {
    color: var(--lightest_grey);
}
a:hover {
    color: var(--light_blue);
}
button {
    background: var(--blue) !important;
    color: #eee;
}
button:active {
    outline: 0.166em solid var(--dark_blue);
}
/* 各种按钮 */
.stdbtn {
    background: none;
    border: none;
    box-shadow: none;
}
.stdbtn .invert {
    background: var(--darkest_grey);
    color: var(--lightest_grey);
}
.stdbtn .invert:hover {
    background: var(--blue);
}
.stdbtn a {
    border: none;
    background: var(--darker_grey);
}
.stdbtn a:hover {
    background-color: var(--blue);
    border-color: var(--dark_blue) !important;
}
.stdbtn a svg {
    fill: var(--blue) !important;
}
.stdbtn a:hover svg {
    fill: var(--lightest_grey) !important;
}
/* ui文字 */
.mmdefault {
    color: var(--light_blue);
}
.mmdefault:hover {
    color: var(--lightest_grey);
}
.uitxt1 {
    color: var(--light_blue);
}
.uitxt1:hover {
    color: var(--lightest_grey);
}
.uitxt2 {
    color: var(--blue);
}
.subtxt_color {
    color: var(--light_blue);
}
/* 顶部 */
#mainmenu .stdbtn .innerbg {
    background: var(--darkest_grey);
}
#mainmenu .stdbtn .td {
    background: var(--darker_grey);
}
/* 板块 */
.catetitle {
    color: var(--blue);
}
.catenew {
    background-color: var(--darkest_grey);
    border: none;
}
.catenew .contentBlock {
    background-color: var(--darkest_grey);
}
.catenew .b2 {
    background-color: var(--darker_grey);
}
.catenew .b3 {
    background-color: var(--darker_grey);
}
.catenew .b4 {
    background-color: var(--darker_grey);
}
.catenew .invert {
    background-color: var(--blue) !important;
}
.catenew p {
    color: var(--light_grey);
}
/* 导航栏 */
.nav_root {
    border: none;
    background: var(--dark_blue);
}
.nav_link {
    border: none;
    color: var(--lightest_grey);
    background: var(--darker_grey);
}
.nav_link:hover, .nav_link:hover .iconfont {
    color: var(--blue);
    fill: var(--blue);
}
/* 快速导航弹窗 */
div[style^='position: absolute'][style*='z-index: 1'] {
    background-color: var(--darkest_grey) !important;
    border-color: var(--grey) !important;
    z-index: 3 !important;
    margin-top: -10px !important;
    margin-left: 40px !important;
}
/* 分页 */
.haveCustomBackground #m_pbtntop .stdbtn {
    background: none;
    border: none;
}
/* 导航固定 */
div#m_nav {
    transform: none;
}
div#m_nav .nav {
    position: fixed;
    z-index: 2;
    top: 223px;
}
div#m_nav .nav_root_c {
    float: unset;
}
div#m_nav .nav_root,div#m_nav .nav_link {
    float: unset;
    width: 130px;
    box-sizing: border-box;
    margin-right: 0;
    overflow: hidden;
    text-wrap: nowrap;
    text-overflow: ellipsis;
}
div#m_nav .nav_root:hover,div#m_nav .nav_link:hover {
    width: unset;
}
/* 边距调整 */
#m_pbtntop, #m_threads, #m_pbtnbtm, #b_nav, #fast_post_c .module_wrap, #m_posts, #m_cate5, #ucp_block, #xoxoxxxoxoxxoo {
    padding: 0 150px;
}
/* 发帖 */
#xoxoxxxoxoxxoo {
    width: unset;
}
#xoxoxxxoxoxxoo .forumbox {
    border-spacing: 0;
}
/* 用户名去除首字母的框框 */
/* .author b:first-child, .replyer b:first-child {
    padding-left: unset !important;
    padding-right: unset !important;
    min-width: unset !important;
    background: unset !important;
    color: unset !important;
    font-weight: normal;
} */
/* 帖子标题右侧小标点 */
.topicrow span[style*='color:#BD7E6D'], .topicrow b[style*='color:#BD7E6D'] {
    color: var(--blue) !important;
}
/* 弹窗 */
.single_ttip2 {
    border: 1px solid var(--grey);
}
.single_ttip2 .colored_text_btn {
    background-color: var(--darkest_grey) !important;
}
.single_ttip2 .colored_text_btn svg {
    fill: var(--lightest_grey) !important;
}
.single_ttip2 .tip_title, .single_ttip2 .div2 {
    background: var(--darkest_grey) !important;
    color: var(--lightest_grey);
    border-bottom: none;
}
/* 预览按钮 */
.block_txt_c0, .block_txt_c0 .iconfont {
    background: var(--dark_blue) !important;
    color: var(--lightest_grey);
    fill: var(--lightest_grey);
}
/* 其他 */
textarea, select, input {
    background: var(--dark_grey) !important;
    border: 1px solid var(--grey) !important;
    color: var(--lightest_grey);
}
option {
    background-color: var(--darkest_grey) !important;
}
img[src*='https://img4.nga.178.com/ngabbs/nga_classic/f/00.png'] {
    filter: invert(1);
}
img[style*='outline'] {
    outline: none !important;
}
/* 控制图片大小 */
/* img {
    max-width: 100px !important;
    max-height: 100px !important;
    min-width: unset !important;
    min-height: unset !important;
} */
/* 背景块  */
.forumbox h2, .forumbox h1 {
    color: var(--light_blue);
}
.forumbox .topicrow .topic_content .postcontent {
    border-color: var(--light_blue);
}
.catenew, .cateblock, .forumbox {
    background-color: var(--darkest_grey);
    border: none;
}
.cateblock .contentBlock, .catenew .contentBlock {
    background-color: var(--darker_grey);
}
}

@-moz-document domain("bbs.nga.cn"), domain("ngabbs.com") {
/* 版块页面 */
/* 论坛首页 */
#m_cate5 .catenew h2 {
    background: none;
}
#m_nav .bbsinfo {
    color: var(--light_grey);
}
/* 版头 */
.contentBlock {
    background-color: var(--darkest_grey);
}
#toptopics {
    border: 1px solid var(--darker_grey);
    box-shadow: 0 0 5px -1px var(--darker_grey);
}
#toptopics table  {
    border-spacing: 10px !important;
    border: none !important;
}
#toptopics table td {
    background-color: var(--darker_grey);
    border: none !important;
    border-radius: 10px;
}
#toptopics * {
    border-top: none !important;
    border-bottom: none !important;
}
#toppedtopic .ubbcode {
    border: none !important;
}
/* 设置滚动条的宽度和颜色 */
#toppedtopic div::-webkit-scrollbar {
    width: 10px;
    background-color: var(--darkest_grey);
    border-radius: 5px;
}

/* 设置滚动条滑块的样式 */
#toppedtopic div::-webkit-scrollbar-thumb {
    background-color: var(--grey);
    border-radius: 5px;
}
/* 子版块 */
#sub_forums_c div[style*='https://img4.nga.178.com/ngabbs/nga_classic/f/00.png'] {
    filter: invert(1);
    background-color: var(--lighter_grey);
    .a {
        filter: invert(1);
    }
}
/* 行表格 */
#topicrows.forumbox {
    border-spacing: 0 5px;
}
#topicrows.forumbox .set_topic_elm .c1 {
    border-color: var(--dark_blue);
}
#topicrows.forumbox .set_topic .c1 {
    border-color: var(--dark_blue);
}
.row1 .c1, .row1 .c3 {
    background-color: var(--darker_grey);
}
.row1 .c2, .row1 .c4 {
